```c
#include<stdio.h>
#include<stdlib.h>
#include<string.h>

union  student
{
  int  age;
  char  *name;
};


int  main(void)
{
  union  student  st;

  //定义指针

  st.name  =  malloc(100);//name指向了一个堆的地址

