            // 登录成功，跳转到首页
            // 这里使用重定向
            resp.sendRedirect("index.jsp");

            // 在回话中保存一个变量，来表示用户已经登录
            HttpSession session = req.getSession();
            // 在session中保存一个变量
            session.setAttribute("islogin", true);
        }else {
            // 登录失败，重新回到登录页面，即回到上一页面
            resp.getWriter().print("<script>window.history.back()</script>");
        }

        // 3. 根据判断结果进行页面跳转，见上；
    }
}

